allow:
Bash(powershell -Command "
$content = Get-Content -Path ''include/core/Application.h'' -Raw
$old = ''    // Search functionality
    void OpenSearch();
    void CloseSearch();
    void SearchNext();
    void SearchPrevious();
    void UpdateSearchResults();

    // URL detection''
$new = ''    // Search functionality
    void OpenSearch();
    void CloseSearch();
    void SearchNext();
    void SearchPrevious();
    void UpdateSearchResults();
    void ToggleSearchRegex();
    void ScrollToMatch(int matchIndex);

    // URL detection''
$content = $content.Replace($old, $new)
Set-Content -Path ''include/core/Application.h'' -Value $content -NoNewline
Write-Host ''Updated Application.h''
")
